mapper.select:
select count(*) from members

	where id like #{keyword}


	where gender like #{keyword}

select * from members

	where id like #{keyword}


	where gender like #{keyword}

order by num desc

select * from members
where
email=#{email} and hp=#{hp}

mapper.update:
update members set
nickname=#{nickname}, email=#{email}, address=#{address}, hp=#{hp}, birth=#{birth},
gender=#{gender}, type=#{type} where id=#{id}
